W[1] = (V[1] + reflect((0,0),W[0])*(V[1]))/2;
V[2] = (W[1] + reflect((0,0),V[0])*(W[1]))/2;
W[2] = (V[2] + reflect((0,0),W[0])*(V[2]))/2;
V[3] = (W[2] + reflect((0,0),V[0])*(W[2]))/2;
W[3] = (V[3] + reflect((0,0),W[0])*(V[3]))/2;

draw((-1,0)--(5,0));
draw((0,-1)--(0,4));
