label("$B$",B,SE);
label("$C$",C,N);
label("$D$",(A+B)/2,S);
label("$E$",(B+C)/2,NE);
label("$O$",O,W);
label("$12\sqrt{3}$",(1,-0.2),S);

[/asy]

[asy]

