for(i=0;i<=5;++i) draw(dir(60*i)--dir(60*(i+1)));
draw(dir(0)--dir(120));
draw(dir(0)--dir(180));
label("$A$",dir(0),dir(0));
label("$B$",dir(120),dir(120));
label("$C$",dir(180),dir(180));
draw(anglemark(dir(60)+dir(180),dir(60),dir(0),3));
[/asy]

