pair[] dots = {A,B,C};
dot(dots);
draw(A--B--C--cycle);
label(rotate(90)*"$5-(-2)$",(0,0.2)+(A+C)/2,W);
label("$10-5$",(B+C)/2,N);
label("$(5,-2)$",A,S);
label("$(10,5)$",B,NE);
label("$(5,5)$",C,NW);
[/asy]

