[asy]
import olympiad; import geometry; size(100); defaultpen(linewidth(0.8)); dotfactor=4;
draw((0,0)--(sqrt(8),0)--(sqrt(2),sqrt(14))--cycle);
dot("$B$",(0,0),W); dot("$A$",(sqrt(2),sqrt(14)),N); dot("$C$",(sqrt(8),0),E);
