draw((0,-1)--(0,3),EndArrow); draw((-1,0)--(9,0),EndArrow); label("$x$",(9,0),E); label("$y$",(0,3),N); size(8cm); dot(A^^B^^O); label("$(0,1)$",B,W);label("$(4,0)$",A,S);label("$(4,1)$",O,E);
