F[1] = (3,7);
F[2] = (49/3,7);
T = (29/3,0);
C = (29/3,7);

draw(ell);
draw((0,-2)--(0,14));
draw((-2,0)--(58/3,0));
draw((0,7)--F[2]--T--F[1]);
draw(C--T);

dot("$C$", C, N);
dot("$F_1$", F[1], N);
dot("$F_2$", F[2], N);
dot("$T$", T, S);
